BEGIN TRY    BEGIN TRAN      DECLARE @report_id_dest INT    
    --RETAIN APPLICATION FILTER DETAILS START (PART1)    
	if object_id('tempdb..#paramset_map') is not null drop table #paramset_map    create table #paramset_map (     deleted_paramset_id int null,      paramset_hash varchar(36) COLLATE DATABASE_DEFAULT NULL,      inserted_paramset_id int null      )    IF EXISTS (SELECT 1 FROM dbo.report WHERE report_hash='9E9D7C44_21C3_430C_82FC_3E3BCCBA2FBA')    BEGIN     declare @report_id_to_delete int     select @report_id_to_delete = report_id from report where report_hash = '9E9D7C44_21C3_430C_82FC_3E3BCCBA2FBA'       insert into #paramset_map(deleted_paramset_id, paramset_hash)     select rp.report_paramset_id, rp.paramset_hash     from report_paramset rp     inner join report_page pg on pg.report_page_id = rp.page_id     where pg.report_id = @report_id_to_delete       EXEC spa_rfx_report @flag='d', @report_id=@report_id_to_delete, @retain_privilege=1, @process_id=NULL          END    --RETAIN APPLICATION FILTER DETAILS END (PART1)    
    declare @report_copy_name varchar(200)        set @report_copy_name = isnull(@report_copy_name, 'Copy of ' + 'Enercity Settlement Invoice Deal Level Report')    
    --RETAIN APPLICATION FILTER DETAILS START (PART2)    update pm    set inserted_paramset_id = rp.report_paramset_id    from #paramset_map pm    inner join report_paramset rp on rp.paramset_hash = pm.paramset_hash        update f set f.report_id = pm.inserted_paramset_id    from application_ui_filter f    inner join #paramset_map pm on pm.deleted_paramset_id = isnull(f.report_id, -1)    where f.application_function_id is null       delete fd    --select *    from application_ui_filter_details fd    inner join application_ui_filter f on f.application_ui_filter_id = fd.application_ui_filter_id    inner join #paramset_map pm on pm.inserted_paramset_id = isnull(f.report_id, -1)    where abs(fd.report_column_id) not in (     select distinct rp.column_id     from report_param rp     inner join report_dataset_paramset rdp on rdp.report_dataset_paramset_id = rp.dataset_paramset_id     inner join report_paramset rpm on rpm.report_paramset_id = rdp.paramset_id     where rpm.report_paramset_id = f.report_id    )    --RETAIN APPLICATION FILTER DETAILS END (PART2)   
COMMIT    END TRY  BEGIN CATCH   IF @@TRANCOUNT > 0    ROLLBACK TRAN;       DECLARE @error_message VARCHAR(MAX) = ERROR_MESSAGE()   RAISERROR(@error_message,16,1)  END CATCH  